<?phprequire_once($_SERVER['DOCUMENT_ROOT'] . "/bitrix/modules/main/include/prolog_before.php");if (isset($_POST['file_ajax_upload'])) {    $files  = $_FILES;    $result = false;    $fileId = false;    // move files from the temporary directory to the specified directory    foreach ($files as $file) {        $fileId = \CFile::SaveFile($file, "my_messenger");        if($fileId) {            $result = true;        }    }    $data = $result ? array('done' => 'y', 'file_id' => $fileId) : array('error' => 'Ошибка загрузки файлов.');    die(json_encode($data));}