<?phpuse Bitrix\Main\Localization\Loc;CUtil::InitJSCore(['jquery']);CModule::IncludeModule('pull');?><div class="chat-window">    <div class="top-bar gainsboro">        <div><?= Loc::getMessage('CHAT_ID', ['#CHAT_ID#' => $arParams['CHAT_ID']])?></div>        <div><?= Loc::getMessage('USER_ID', ['#USER_ID#' => $USER->GetID()])?></div>    </div>    <div class="window">        <div class="users-in-chat gainsboro">            <?foreach ($arResult['USERS_IN_CHAT'] as $userId):?>                <div class="user"><?= $userId?></div>            <?endforeach;?>        </div>        <div class="messages">            <?foreach ($arResult['LAST_MESSAGES'] as $arMessage):?>                <div class="single-message" data-id="<?= $arMessage['ID']?>">                    <?= $arMessage['OUTPUT_DATA'];?>                </div>            <?endforeach;?>        </div>    </div>    <div class="reply-container hide">        <div class="reply-message">            <div class="background"></div>            <div class="message-text"></div>            <div class="close-reply">x</div>        </div>    </div>    <div class="inputs gainsboro">        <div class="text-message">            <textarea name="message" id="message" cols="30" rows="10"></textarea>        </div>        <div class="button">            <input type="hidden" id="files_">            <input type="file" name="file" id="file_input">            <div id="send_file"><?= Loc::getMessage('SEND_FILE')?></div>            <button id="send_button"><?= Loc::getMessage('SEND')?></button>        </div>    </div></div><script>    var signerParams = '<?= $this->getComponent()->getSignedParameters()?>';    var componentName = '<?= $this->getComponent()->getName()?>';</script>